var MongoCliect = require('mongodb').MongoClient;
var config = require('informations.config');

var Db={
  url: "mongodb://"+config.user+":"+config.passward+"@"+config.host+":"+config.port+"/"+config.db_name,
  db: undefined,
  connect: function(callback){
    var self = this;
    MongoCliect.connect(self.url, function(err, database){
      if(err) throw err;
      self.db = database;

      callback();
    });
  },
}

module.exports=Db;
