var MongoCliect = require('mongodb').MongoClient;
var config = require('informations.config');

var Db={
  url: "mongodb://"+config.user+":"+config.password+"@"+config.host+":"+config.port+"/"+config.db_name,
  db: undefined,
  connect: function(callback){
    MongoCliect.connect(Db.url, function(err, database){
      if(err) console.log(err);
      Db.db = database;

      callback();
    });
  },
}

module.exports=Db;
