var Db = require('Database');

module.exports = function(req, res, is_login){
	if(is_login){
		var count = Db.db.collection('users').find({email: req.cookie['email'], verified: false}).count();
		count ? res.end(JSON.stringify({valid: false})) : res.end(JSON.stringify({valid: true}));
		return;
	}else{
		res.end(JSON.stringify({message: 'you are not login'}))
		return;
	}
}
