module.exports = function(req, res, collection){
  var result={};
  var id,title,content,options;
  var db_question,cursor;
  var request_num = Number(req.query.num);
  if(!request_num) res.end('Request Error');
  cursor = collection.find({"id": request_num});
  cursor.toArray(function(err, docs){
    if(err) throw err;
    if(docs.length===0) res.end('no such question(id error)');
    db_question = docs[0];
    console.dir(db_question);
    id = db_question.id;
    title = db_question.title;
    content = db_question.content;
    options = db_question.options;
    result = {id: id,title: title,content: content,options: options};
    res.end(JSON.stringify(result));
  });
}
