module.exports = function(req, res, collection){
  var result={};
  var id,title,content,options,tag,pic;
  var db_question,cursor;
  var request_num = req.query.num;
  if(!request_num){
  	res.end(JSON.stringify({message: 'Request Error'}));
  	return;
  }
  cursor = collection.find({"id": request_num});
  cursor.toArray(function(err, docs){
    if(err){
			console.log(err);
			return;
		}
    if(docs.length===0){
			res.end(JSON.stringify({message:'no such question(id error)'}));
			return;
		}
    db_question = docs[0];
    //console.dir(db_question);
    id = db_question.id;
    title = db_question.title;
    content = db_question.content;
    options = db_question.options;
		tag = db_question.tag;
		pic = db_question.pic;
    result = {id: id,title: title,content: content,options: options,tag: tag,pic: pic};
    res.end(JSON.stringify(result));
		return;
  });
}
