module.exports = function(req, res, collection){
  var result=[];
  var id,title,content,options;
  var db_questions,cursor;
  cursor = collection.find({});
  cursor.toArray(function(err, docs){
    if(err){
			console.log(err);
			return;
		}
    if(docs.length===0){
			res.end(JSON.stringify({message:'no problems??!'}));
			return;
		}
    db_questions = docs;
		db_questions.forEach(function(db_question){
			id = db_question.id;
			title = db_question.title;
			content = db_question.content;
			options = db_question.options;
			result.push({id: id,title: title,content: content,options: options});
		});
	res.end(JSON.stringify(result));
	return;
  });
}
