var Db = require('Database');

module.exports = function(req, res){
  var id = req.params.id;
  Db.db.collection('profiles').find({nickname: id}).toArray(function(err, docs){
    if(err){
			console.log(err);
			res.end({message: 'sth went wrong!'})
			return;
		}
    if(docs.length!==1){
      res.end(JSON.stringify({message: 'profiles.length!==1'}));
			return;
		}
    var doc = docs[0];
    res.end(JSON.stringify(doc));
  });
}
