var Db = require('Database').db;

module.exports = function(req, res){
  var id = req.params.id;
  Db.collection('profiles').find({nickname: id}).toArray(function(err, docs){
    if(err) throw err;
    if(docs.length!==1)
      res.end(JSON.stringify({message: 'profiles.length!==1'}));
    var doc = docs[0];
    doc.message='';
    res.json(doc);
  });
}
