var config = require('informations.config');
var Db = require('Database');

module.exports = function(email, id/*problem id*/, stat/*false for WA, true for AC*/){
        if(err){
					console.log(err);
					return;
				}
        if(stat){
            Db.db.collection('profiles').update(
                {email: email},
                {$push: {AC: id}}
            )
        }
        else{
            Db.db.collection('profiles').update(
                {email: email},
                {$push: {WA: id}}
            )
        }
}
