var recaptcha = require('recaptcha_v2');
var config = require('informations.config');
var secretToken = config.secretToken;
module.exports = function(token){
	console.log('verifyRecaptcha.js config.secretToken&token')
	console.log(config.secretToken);
	console.log(token)
  var captcha = new recaptcha({
    secret: secretToken,
    response: token
  });
  captcha.verify(function(err, done){
    if(err){
			console.log(err);
			return;
		}
    return done&&true;
  });
}
